mapper.select:
SELECT d.postIdx, d.postDailyTitle, substring(d.postDailyContents, 1, 10) as postDaily, d.memberIdx, d.postDailyRegDate, d.postDailyEditDate, d.postDailyHits, m.memberNickname,p.boardIdx,m.memberEmail FROM postDaily AS d JOIN member AS m ON d.memberIdx = m.memberIdx JOIN post AS p ON d.postIdx = p.postIdx WHERE p.boardIdx = #{boardIdx}
AND d.postDailyTitle LIKE concat('%', #{keyword}, '%')
AND d.postDailyContents LIKE concat('%', #{keyword}, '%')
AND (d.postDailyTitle LIKE concat('%', #{keyword}, '%') OR d.postDailyContents like concat('%', #{keyword}, '%'))
AND m.memberNickname LIKE concat('%', #{keyword}, '%')
ORDER BY d.postDailyRegDate DESC limit #{postsPerPage} offset #{offset}

mapper.insert:
insert into post(boardIdx)
values(#{boardIdx})

    select last_insert_id();

mapper.update:
UPDATE postDaily
SET postDailyTitle=#{postDailyTitle}, postDailyContents=#{postDailyContents}, postDailyEditDate= now()
WHERE postIdx=#{postIdx}

UPDATE postDaily
set postDailyHits = postDailyHits+1
WHERE postIdx=#{postIdx}
